import React, { useState, useEffect } from 'react';
import './index.css';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Home Page Component
function HomePage({ content, amenities, faqs, updates, onAdminClick }) {
  const [openFaq, setOpenFaq] = useState(null);

  const toggleFaq = (index) => {
    setOpenFaq(openFaq === index ? null : index);
  };

  const getContent = (section, field) => {
    const sectionData = content.find(c => c.section === section);
    return sectionData ? sectionData[field] : '';
  };

  const getAmenityIcon = (icon) => {
    const icons = {
      pool: 'üèä',
      gym: 'üí™',
      park: 'üå≥',
      security: 'üîí',
      parking: 'üÖøÔ∏è',
      club: 'üè†'
    };
    return icons[icon] || '‚≠ê';
  };

  return (
    <>
      {/* Navbar */}
      <nav className="navbar">
        <a href="#" className="logo">MEGAPLEX<span>PRIME</span></a>
        <ul className="nav-links">
          <li><a href="#overview">Overview</a></li>
          <li><a href="#connectivity">Connectivity</a></li>
          <li><a href="#amenities">Amenities</a></li>
          <li><a href="#about">About</a></li>
          <li><a href="#updates">Updates</a></li>
          <li><a href="#faq">FAQ</a></li>
        </ul>
        <button className="admin-btn" onClick={onAdminClick}>Admin</button>
      </nav>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>{getContent('hero', 'title')}</h1>
          <h2>{getContent('hero', 'subtitle')}</h2>
          <p>{getContent('hero', 'description')}</p>
          <a href="#overview" className="cta-button">Explore Now</a>
        </div>
      </section>

      {/* Overview Section */}
      <section id="overview" className="section overview">
        <div className="section-title">
          <h2>{getContent('overview', 'title')}</h2>
          <h3>{getContent('overview', 'subtitle')}</h3>
        </div>
        <div className="overview-content">
          <div className="overview-text">
            <h4>Welcome to Megaplex Prime</h4>
            <p>{getContent('overview', 'description')}</p>
            <p>{getContent('overview', 'content')}</p>
          </div>
          <div className="overview-image">
            <span>Project Preview Image</span>
          </div>
        </div>
      </section>

      {/* Connectivity Section */}
      <section id="connectivity" className="section connectivity">
        <div className="section-title">
          <h2>{getContent('connectivity', 'title')}</h2>
          <h3>{getContent('connectivity', 'subtitle')}</h3>
        </div>
        <div className="connectivity-grid">
          <div className="connectivity-item">
            <h4>üöá Metro</h4>
            <p>500 meters from Metro Station</p>
          </div>
          <div className="connectivity-item">
            <h4>‚úàÔ∏è Airport</h4>
            <p>15 km from International Airport</p>
          </div>
          <div className="connectivity-item">
            <h4>ÔøΩÁÅ´ËΩ¶Á´ô</h4>
            <p>3 km from Railway Station</p>
          </div>
          <div className="connectivity-item">
            <h4>üõçÔ∏è Mall</h4>
            <p>1 km from Shopping Mall</p>
          </div>
          <div className="connectivity-item">
            <h4>üè• Hospital</h4>
            <p>2 km from Multi-specialty Hospital</p>
          </div>
          <div className="connectivity-item">
            <h4>üéì School</h4>
            <p>1.5 km from renowned Schools</p>
          </div>
        </div>
      </section>

      {/* Amenities Section */}
      <section id="amenities" className="section amenities">
        <div className="section-title">
          <h2>{getContent('amenities', 'title')}</h2>
          <h3>{getContent('amenities', 'subtitle')}</h3>
        </div>
        <div className="amenities-grid">
          {amenities.map((amenity, index) => (
            <div className="amenity-card" key={index}>
              <div className="amenity-icon">{getAmenityIcon(amenity.icon)}</div>
              <h4>{amenity.title}</h4>
              <p>{amenity.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="section about">
        <div className="section-title">
          <h2>{getContent('about', 'title')}</h2>
          <h3>{getContent('about', 'subtitle')}</h3>
        </div>
        <div className="about-content">
          <div className="about-image">
            <span>About Us Image</span>
          </div>
          <div className="about-text">
            <h4>Building Your Dreams</h4>
            <p>{getContent('about', 'description')}</p>
            <p>{getContent('about', 'content')}</p>
          </div>
        </div>
      </section>

      {/* Construction Updates Section */}
      <section id="updates" className="section updates">
        <div className="section-title">
          <h2>{getContent('construction', 'title')}</h2>
          <h3>{getContent('construction', 'subtitle')}</h3>
        </div>
        <div className="updates-timeline">
          {updates.map((update, index) => (
            <div className={`update-item ${update.status}`} key={index}>
              <h4>{update.title}</h4>
              <p>{update.description}</p>
              <span className="update-date">{update.date}</span>
              <span className={`update-status ${update.status}`}>
                {update.status === 'completed' ? 'Completed' : 
                 update.status === 'in-progress' ? 'In Progress' : 'Upcoming'}
              </span>
            </div>
          ))}
        </div>
      </section>

      {/* FAQ Section */}
      <section id="faq" className="section faq">
        <div className="section-title">
          <h2>{getContent('faq', 'title')}</h2>
          <h3>{getContent('faq', 'subtitle')}</h3>
        </div>
        <div className="faq-container">
          {faqs.map((faq, index) => (
            <div className="faq-item" key={index}>
              <div className="faq-question" onClick={() => toggleFaq(index)}>
                <span>{faq.question}</span>
                <span>{openFaq === index ? '‚àí' : '+'}</span>
              </div>
              {openFaq === index && (
                <div className="faq-answer">
                  <p>{faq.answer}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 Megaplex Prime. All Rights Reserved.</p>
        <p>Developed with ‚ù§Ô∏è by <a href="#">Megaplex Group</a></p>
      </footer>
    </>
  );
}

// Login Component
function Login({ onLogin, onBack }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch(`${API_BASE}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      const data = await response.json();

      if (data.success) {
        onLogin();
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError('Connection error. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Admin Login</h2>
        {error && <p className="login-error">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter email"
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
        <button 
          onClick={onBack}
          style={{ marginTop: '15px', background: 'transparent', border: 'none', color: '#666', cursor: 'pointer', width: '100%' }}
        >
          Back to Website
        </button>
      </div>
    </div>
  );
}

// Admin Panel Component
function AdminPanel({ onLogout }) {
  const [content, setContent] = useState([]);
  const [amenities, setAmenities] = useState([]);
  const [faqs, setFaqs] = useState([]);
  const [updates, setUpdates] = useState([]);
  const [activeSection, setActiveSection] = useState('hero');
  const [editingItem, setEditingItem] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [contentRes, amenitiesRes, faqsRes, updatesRes] = await Promise.all([
        fetch(`${API_BASE}/content`),
        fetch(`${API_BASE}/amenities`),
        fetch(`${API_BASE}/faqs`),
        fetch(`${API_BASE}/updates`)
      ]);

      setContent(await contentRes.json());
      setAmenities(await amenitiesRes.json());
      setFaqs(await faqsRes.json());
      setUpdates(await updatesRes.json());
    } catch (err) {
      console.error('Error fetching data:', err);
    }
  };

  const updateContent = async (section, data) => {
    try {
      await fetch(`${API_BASE}/content/${section}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      fetchData();
      alert('Content updated successfully!');
    } catch (err) {
      console.error('Error updating content:', err);
    }
  };

  const updateAmenity = async (id, data) => {
    try {
      await fetch(`${API_BASE}/amenities/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      fetchData();
      setEditingItem(null);
      alert('Amenity updated successfully!');
    } catch (err) {
      console.error('Error updating amenity:', err);
    }
  };

  const updateFaq = async (id, data) => {
    try {
      await fetch(`${API_BASE}/faqs/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      fetchData();
      setEditingItem(null);
      alert('FAQ updated successfully!');
    } catch (err) {
      console.error('Error updating FAQ:', err);
    }
  };

  const addFaq = async () => {
    try {
      await fetch(`${API_BASE}/faqs`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: 'New Question', answer: 'New Answer' })
      });
      fetchData();
      alert('FAQ added successfully!');
    } catch (err) {
      console.error('Error adding FAQ:', err);
    }
  };

  const deleteFaq = async (id) => {
    if (!window.confirm('Are you sure you want to delete this FAQ?')) return;
    try {
      await fetch(`${API_BASE}/faqs/${id}`, { method: 'DELETE' });
      fetchData();
      alert('FAQ deleted successfully!');
    } catch (err) {
      console.error('Error deleting FAQ:', err);
    }
  };

  const updateConstructionUpdate = async (id, data) => {
    try {
      await fetch(`${API_BASE}/updates/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      fetchData();
      setEditingItem(null);
      alert('Update saved successfully!');
    } catch (err) {
      console.error('Error saving update:', err);
    }
  };

  const getContent = (section) => {
    return content.find(c => c.section === section) || {};
  };

  const renderContentEditor = () => {
    const sectionData = getContent(activeSection);

    switch (activeSection) {
      case 'hero':
      case 'overview':
      case 'connectivity':
      case 'amenities':
      case 'about':
      case 'construction':
      case 'faq':
        return (
          <div className="admin-section">
            <h2>Edit {activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Section</h2>
            <div className="form-group">
              <label>Title</label>
              <input
                type="text"
                defaultValue={sectionData.title || ''}
                onChange={(e) => sectionData.title = e.target.value}
              />
            </div>
            <div className="form-group">
              <label>Subtitle</label>
              <input
                type="text"
                defaultValue={sectionData.subtitle || ''}
                onChange={(e) => sectionData.subtitle = e.target.value}
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                defaultValue={sectionData.description || ''}
                onChange={(e) => sectionData.description = e.target.value}
              />
            </div>
            {(activeSection === 'overview' || activeSection === 'about') && (
              <div className="form-group">
                <label>Content</label>
                <textarea
                  defaultValue={sectionData.content || ''}
                  onChange={(e) => sectionData.content = e.target.value}
                />
              </div>
            )}
            <button className="submit-btn" onClick={() => updateContent(activeSection, {
              title: sectionData.title,
              subtitle: sectionData.subtitle,
              description: sectionData.description,
              content: sectionData.content
            })}>Save Changes</button>
          </div>
        );

      case 'amenities-edit':
        return (
          <div className="admin-section">
            <h2>Edit Amenities</h2>
            <div className="editable-list">
              {amenities.map((amenity) => (
                <div className="editable-item" key={amenity.id}>
                  {editingItem === `amenity-${amenity.id}` ? (
                    <>
                      <div className="form-group">
                        <label>Title</label>
                        <input
                          type="text"
                          defaultValue={amenity.title}
                          id={`amenity-title-${amenity.id}`}
                        />
                      </div>
                      <div className="form-group">
                        <label>Description</label>
                        <textarea
                          defaultValue={amenity.description}
                          id={`amenity-desc-${amenity.id}`}
                        />
                      </div>
                      <div className="editable-item-actions">
                        <button className="edit-btn" onClick={() => updateAmenity(amenity.id, {
                          title: document.getElementById(`amenity-title-${amenity.id}`).value,
                          description: document.getElementById(`amenity-desc-${amenity.id}`).value
                        })}>Save</button>
                        <button className="delete-btn" onClick={() => setEditingItem(null)}>Cancel</button>
                      </div>
                    </>
                  ) : (
                    <>
                      <h4>{amenity.title}</h4>
                      <p>{amenity.description}</p>
                      <div className="editable-item-actions">
                        <button className="edit-btn" onClick={() => setEditingItem(`amenity-${amenity.id}`)}>Edit</button>
                      </div>
                    </>
                  )}
                </div>
              ))}
            </div>
          </div>
        );

      case 'faqs-edit':
        return (
          <div className="admin-section">
            <h2>Edit FAQs</h2>
            <div className="editable-list">
              {faqs.map((faq) => (
                <div className="editable-item" key={faq.id}>
                  {editingItem === `faq-${faq.id}` ? (
                    <>
                      <div className="form-group">
                        <label>Question</label>
                        <input
                          type="text"
                          defaultValue={faq.question}
                          id={`faq-question-${faq.id}`}
                        />
                      </div>
                      <div className="form-group">
                        <label>Answer</label>
                        <textarea
                          defaultValue={faq.answer}
                          id={`faq-answer-${faq.id}`}
                        />
                      </div>
                      <div className="editable-item-actions">
                        <button className="edit-btn" onClick={() => updateFaq(faq.id, {
                          question: document.getElementById(`faq-question-${faq.id}`).value,
                          answer: document.getElementById(`faq-answer-${faq.id}`).value
                        })}>Save</button>
                        <button className="delete-btn" onClick={() => setEditingItem(null)}>Cancel</button>
                      </div>
                    </>
                  ) : (
                    <>
                      <h4>{faq.question}</h4>
                      <p>{faq.answer}</p>
                      <div className="editable-item-actions">
                        <button className="edit-btn" onClick={() => setEditingItem(`faq-${faq.id}`)}>Edit</button>
                        <button className="delete-btn" onClick={() => deleteFaq(faq.id)}>Delete</button>
                      </div>
                    </>
                  )}
                </div>
              ))}
            </div>
            <button className="add-btn" onClick={addFaq}>Add New FAQ</button>
          </div>
        );

      case 'updates-edit':
        return (
          <div className="admin-section">
            <h2>Edit Construction Updates</h2>
            <div className="editable-list">
              {updates.map((update) => (
                <div className="editable-item" key={update.id}>
                  {editingItem === `update-${update.id}` ? (
                    <>
                      <div className="form-group">
                        <label>Title</label>
                        <input
                          type="text"
                          defaultValue={update.title}
                          id={`update-title-${update.id}`}
                        />
                      </div>
                      <div className="form-group">
                        <label>Description</label>
                        <textarea
                          defaultValue={update.description}
                          id={`update-desc-${update.id}`}
                        />
                      </div>
                      <div className="form-group">
                        <label>Date</label>
                        <input
                          type="date"
                          defaultValue={update.date}
                          id={`update-date-${update.id}`}
                        />
                      </div>
                      <div className="form-group">
                        <label>Status</label>
                        <select
                          defaultValue={update.status}
                          id={`update-status-${update.id}`}
                          style={{ width: '100%', padding: '12px', border: '1px solid #ddd', borderRadius: '5px' }}
                        >
                          <option value="upcoming">Upcoming</option>
                          <option value="in-progress">In Progress</option>
                          <option value="completed">Completed</option>
                        </select>
                      </div>
                      <div className="editable-item-actions">
                        <button className="edit-btn" onClick={() => updateConstructionUpdate(update.id, {
                          title: document.getElementById(`update-title-${update.id}`).value,
                          description: document.getElementById(`update-desc-${update.id}`).value,
                          date: document.getElementById(`update-date-${update.id}`).value,
                          status: document.getElementById(`update-status-${update.id}`).value
                        })}>Save</button>
                        <button className="delete-btn" onClick={() => setEditingItem(null)}>Cancel</button>
                      </div>
                    </>
                  ) : (
                    <>
                      <h4>{update.title}</h4>
                      <p>{update.description}</p>
                      <span className={`update-status ${update.status}`}>
                        {update.status === 'completed' ? 'Completed' : 
                         update.status === 'in-progress' ? 'In Progress' : 'Upcoming'}
                      </span>
                      <div className="editable-item-actions">
                        <button className="edit-btn" onClick={() => setEditingItem(`update-${update.id}`)}>Edit</button>
                      </div>
                    </>
                  )}
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return <div>Select a section to edit</div>;
    }
  };

  return (
    <div className="admin-container">
      <nav className="admin-navbar">
        <h1>Admin Panel - Megaplex Prime</h1>
        <button onClick={onLogout}>Logout</button>
      </nav>
      <div className="admin-content">
        <div style={{ display: 'grid', gridTemplateColumns: '250px 1fr', gap: '30px' }}>
          <div>
            <div className="admin-section">
              <h2>Sections</h2>
              <div className="form-group">
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'hero' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('hero')}
                >
                  Hero Section
                </button>
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'overview' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('overview')}
                >
                  Project Overview
                </button>
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'connectivity' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('connectivity')}
                >
                  Connectivity
                </button>
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'amenities-edit' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('amenities-edit')}
                >
                  Amenities
                </button>
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'about' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('about')}
                >
                  About Us
                </button>
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'updates-edit' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('updates-edit')}
                >
                  Construction Updates
                </button>
                <button 
                  className="submit-btn" 
                  style={{ width: '100%', marginBottom: '10px', background: activeSection === 'faqs-edit' ? '#e94560' : '#1a1a2e' }}
                  onClick={() => setActiveSection('faqs-edit')}
                >
                  FAQs
                </button>
              </div>
            </div>
          </div>
          <div>
            {renderContentEditor()}
          </div>
        </div>
      </div>
    </div>
  );
}

// Main App Component
function App() {
  const [view, setView] = useState('home'); // 'home', 'login', 'admin'
  const [content, setContent] = useState([]);
  const [amenities, setAmenities] = useState([]);
  const [faqs, setFaqs] = useState([]);
  const [updates, setUpdates] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [contentRes, amenitiesRes, faqsRes, updatesRes] = await Promise.all([
        fetch(`${API_BASE}/content`),
        fetch(`${API_BASE}/amenities`),
        fetch(`${API_BASE}/faqs`),
        fetch(`${API_BASE}/updates`)
      ]);

      setContent(await contentRes.json());
      setAmenities(await amenitiesRes.json());
      setFaqs(await faqsRes.json());
      setUpdates(await updatesRes.json());
    } catch (err) {
      console.error('Error fetching data:', err);
    }
  };

  const handleAdminClick = () => {
    setView('login');
  };

  const handleLogin = () => {
    setView('admin');
  };

  const handleLogout = () => {
    setView('home');
  };

  const handleBack = () => {
    setView('home');
  };

  switch (view) {
    case 'login':
      return <Login onLogin={handleLogin} onBack={handleBack} />;
    case 'admin':
      return <AdminPanel onLogout={handleLogout} />;
    default:
      return (
        <HomePage
          content={content}
          amenities={amenities}
          faqs={faqs}
          updates={updates}
          onAdminClick={handleAdminClick}
        />
      );
  }
}

export default App;
